#include <stdio.h>
#include <stdlib.h>
#include <string.h>


/**
 * Return an array of size *returnSize.
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* findSubstring(char* s, char** words, int wordsSize, int* returnSize) {
    if(!s || !words || !wordsSize) {
        *returnSize = 0;
        return NULL;
    }

    int wordLen = strlen(words[0]);
    int sLen = strlen(s);
    int maxN = sLen / wordLen;
    int i, j, k;
    int index;
    int count;

    int *retIndexs = (int *)malloc(maxN * sizeof(int));
    char *tmpWord = (char *)malloc(wordLen + 1);
    char *tmpIndex = (char *)malloc(wordsSize + 1);

    index = 0;
    for(i = 0; (i + wordsSize * wordLen) <= sLen; i++) {
        memset(tmpIndex, 0x1, wordsSize);
        for(j = 0; j < wordsSize; j++) {
            memcpy(tmpWord, s + i + j * wordLen, wordLen);
            tmpWord[wordLen] = '\0';

            for(k = 0; k < wordsSize; k++) {
                if(tmpIndex[k] == 0x1 && strcmp(tmpWord, words[k]) == 0) {
                    tmpIndex[k] = 0;
                    break;
                }
            }

            if(k == wordsSize) {
                break;
            }
        }

        if(j == wordsSize) {
            retIndexs[index++] = i;
        }
    }

    free(tmpWord);
    free(tmpIndex);

    *returnSize = index;
    return retIndexs;
}

int main()
{
    return 0;
}

